# Pseudocode
class BinaryHeap:
    def __init__(self):
        self.heap = []

    def insert(self, value):
        self.heap.append(value)
        self.bubble_up(len(self.heap) - 1)

    def delete(self):
        if len(self.heap) > 1:
            self.swap(0, len(self.heap) - 1)
            max_value = self.heap.pop()
            self.bubble_down(0)
        elif len(self.heap) == 1:
            max_value = self.heap.pop()
        else:
            max_value = None
        return max_value

    def bubble_up(self, index):
        parent_index = (index - 1) // 2
        if index <= 0 or self.heap[parent_index] >= self.heap[index]:
            return
        else:
            self.swap(index, parent_index)
            self.bubble_up(parent_index)

    def bubble_down(self, index):
        child_index = 2 * index + 1
        if child_index >= len(self.heap):
            return
        # If there's a right child, and it's greater than left child
        if child_index + 1 < len(self.heap) and self.heap[child_index] < self.heap[child_index + 1]:
            child_index += 1
        # If the child node is smaller than the current node
        if self.heap[child_index] <= self.heap[index]:
            return
        else:
            self.swap(index, child_index)
            self.bubble_down(child_index)

    def swap(self, i, j):
        self.heap[i], self.heap[j] = self.heap[j], self.heap[i]
